INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (1,'Metropolis',2,1000,'Perfectly located on Ste. Catherine St. in the heart of the Quartier des Spectacles, MTELUS spent previous incarnations as an opera house, playhouse, movie theatre, discotheque and even an ice skating rink! Now a genuine temple of music, it has hosted the biggest artists from home and abroad, from the great Sarah Bernhardt to Adele, from Prince to Lady Gaga.',1,'Montreal','H2L2C4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (2,'Spectrum',2,500,'The Spectrum opened on October 17, 1982, as the Alouette Theatre, it was briefly renamed Club Montreal before receiving its popular name.',2,'Montreal','H2L2C4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (3,'Bell Centre',1,800,'Since its inauguration in 1996 under the name Molson Centre, the Centre Bell remains the centre stage for entertainment and business events in Montreal. The Centre Bell incorporates cutting-edge technology to ensure the perfect ambience when it comes to sound, comfort, catering and otherwise for any event, whatever its magnitude.',3,'Montreal','H2L2C4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (4,'Videotron Centre',1,200,'. Throughout the site, you can experience the warm, cozy atmosphere of chalets, seamlessly blended with sleek, chic spaces with materials like steel and wood. The metal coating used in construction reflects light, like snow or the waves on the river, while the blue and white colours throughout pay tribute to the world-famous Quebec winter.',4,'Ottawa','K1N6N5');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (5,'Foufounes Electriques',3,615,'Rental includes: a sound technician, a lighting technician (Main Show Room only), a door person, all bar staff and security.',5,'Ottawa','K1N6N5');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (6,'Salon Daome',3,200,'Laid-back nightclub with a roster of international DJs spinning electronic music, plus theme nights.',6,'Ottawa','K1N6N5');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (7,'Taco Bar',3,350,'This restaurant is located in Monkland village. The restaurant has a lot of open space.  Plenty of room to add more tables if needed.',7,'Vancouver','V6T1Z4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (8,'PK-310',4,50,'Students can rent out this room, free of charge, provided they have their student card with them.',8,'Vancouver','V6T1Z4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (9,'PK-620',4,30,'Students can rent out this room, free of charge, provided they have their student card with them.',9,'Vancouver','V6T1Z4');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (10,'PK-420',4,200,'Students can rent out this room, free of charge, provided they have their student card with them.',10,'Edmonton','T6G2R3');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (11,'Saint-Siboire',3,100,'With a constantly evolving selection of beers, a bistro-style food menu and a range of scotchs and whiskeys, our mission is to make you discover and appreciate the brewing world in all its facets. You can even satisfy your seafood appetite with one of the best Fish & Chips in the province.',11,'Saskatoon','S7N5A2');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (12,'Theatre du nouveau monde',5,200, 'The TNM .... a place of prestige for your small and big events! A unique and original atmosphere! Stamp and style! An incredible service! A successful event!',12,'Winnipeg','R3T2N2');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (13,'Theatre National',5,300,'Make sure you have a place to sit with your friends on Fridays and Saturdays during POP nights, by reserving our new VIP corner! In this zone only, you can purchase alcohol by the bottle.  We also make sure you get into the venue fast as well as receiving two complimentary entries.',13,'Halifax','B3H4R2');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (14,'Centre culturel Marcel',6,200,'The Town of Saint-Bruno-de-Montarville is partnering with Productions du 16 to present professional shows of all kinds at the Marcel-Dulude Center, be it humor, theater or song.',14,'Fredericton','E3B5A3');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (15,'Home',7,50,'The only place in the world that feels like home, because, it is. Unfortunately, this room cannot be rented, as it is my home.',15,'Charlotettown','C1A4P3');
INSERT INTO Room (id, name, type, capacity, description, equipment_id, city, postalCode) VALUES (16,'Plaines Abraham',8,1000,'One hundred and three hectares of meadow and grassy knolls, decked with flowers or covered with snow, are there for residents and visitors to enjoy.',16,'Saint-John','A1C5S7');
